    <span class="token literal-property property">dest</span><span class="token operator">:</span> <span class="token function">resolve</span><span class="token punctuation">(</span><span class="token string">'dist/vue.esm.js'</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
