        <span class="token keyword">return</span> doc<span class="token punctuation">.</span><span class="token function">createTextNode</span><span class="token punctuation">(</span>vdom<span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token punctuation">}</span>

