    extendOptions <span class="token operator">=</span> extendOptions <span class="token operator">||</span> <span class="token punctuation">{</span><span class="token punctuation">}</span> <span class="token comment">//new Vue()时传入的组件对象</span>
    <span class="token comment">/*父类的构造*/</span>
